UPDATE configuration SET configuration_title='商品リストのトップカテゴリ画像', configuration_description='商品リストのトップカテゴリで画像を表示しますか?\r\n<br />\r\n・true (する)<br />\r\n・false (しない)' WHERE configuration_key='PRODUCT_LIST_CATEGORIES_IMAGE_STATUS_TOP';
UPDATE configuration SET configuration_title='トップカテゴリ内サブカテゴリのリンク表示', configuration_description='トップカテゴリページでサブカテゴリのリンクを表示するかどうか設定します。<br />\r\n・0=非表示<br />\r\n・1=表示' WHERE configuration_key='PRODUCT_LIST_CATEGORY_ROW_STATUS';
UPDATE configuration SET configuration_title='在庫水準のチェック', configuration_description='十分な在庫があるかチェックするかどうかを設定します。' WHERE configuration_key='STOCK_CHECK';
