UPDATE configuration SET configuration_title='メールアドレスをDNSで確認', configuration_description='メールアドレスをDNSサーバに問い合わせ確認するかどうかを設定します。' WHERE configuration_key='ENTRY_EMAIL_ADDRESS_CHECK';
